import { useState } from 'react'

const Heading = ({sentence}) => (
  <h1>{sentence}</h1>
)

const Button = (props) => (
  <button onClick={props.handleClick}>{props.text}</button>
)

const Stats = ({text, value}) => (
  <p>{text} {value}</p>
)

const Percentage = (props) => {
  const text = props.text
  const all = props.good+props.bad+props.neutral
  const averageGood = (props.good)/all
  const score = (props.good*1)+(props.bad*-1)


  if (text=="average"){
    return (
      <p>{text} {score/3}</p>
    )
  }
  else if (text=="positive"){
    if((props.good + props.neutral + props.bad)==0){
      return (
        <p>0 %</p>
      )
    }
    else{
      return (
        <p>{text} {averageGood*100} %</p>
      )
    }
  }
}


const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <Heading sentence="give feedback"/>
      <Button handleClick={() => setGood(good+1)} text="good"/>
      <Button handleClick={() => setNeutral(neutral+1)} text="neutral"/>
      <Button handleClick={() => setBad(bad+1)} text="bad"/>
      <Heading sentence="statistics"/>
      <Stats text="good" value={good}/>
      <Stats text="neautral" value={neutral}/>
      <Stats text="bad" value={bad}/>
      <Stats text="all" value={good+bad+neutral}/>
      <Percentage text="average" good={good} bad={bad} neutral={neutral} />
      <Percentage text="positive" good={good} bad={bad} neutral={neutral} />
    </div>
  )
}

export default App
